import { Component, OnInit } from '@angular/core';

@Component({
    selector: 'app-detalles-obra', // Asegúrate de tener el selector correcto
    templateUrl: './detalles-obra.component.html',
    styleUrls: ['./detalles-obra.component.css']
})
export class DetallesObraComponent implements OnInit {
    obra: any;  // Asume que tienes un objeto obra
    cantidadReservas: number = 1;

    constructor() { }

    ngOnInit(): void {
        // Cargar los detalles de la obra
    }

    confirmarReserva() {
        console.log(`Reservadas ${this.cantidadReservas} entradas para ${this.obra.titulo}`);
        // Aquí añades la lógica para manejar la reserva (p.ej., enviar a una API)
    }
}
<div class="obra-details">
  <h1>{{ obra.titulo }}</h1>
  <img [src]="obra.imagenUrl" alt="{{ obra.titulo }}">
  <p>{{ obra.descripcion }}</p>

  <div class="reserva-box">
      <label for="reservaCantidad">Elige la cantidad de reservas (1-5):</label>
      <select id="reservaCantidad" [(ngModel)]="cantidadReservas">
          <option *ngFor="let numero of [1,2,3,4,5]" [value]="numero">{{numero}}</option>
      </select>
      <button (click)="confirmarReserva()">Confirmar Reserva</button>
  </div>
</div>
