import { Component, OnInit } from '@angular/core';
import { ObraService } from '../services/obra.service';
import { AuthenticationService } from '../services/authentication.service';
 

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})


export class HomeComponent implements OnInit {
  title = 'TeatroSinFiltros';
  shows: any[] = [];

  constructor(private obraService: ObraService,  public authService: AuthenticationService 
    ) { }

  ngOnInit() {
    this.obraService.getObras().subscribe(
      (data) => {
        console.log(data);
        this.shows = data;
      },
      (error) => {
        console.error('Error al obtener obras', error);
      }
    );
  }
  
  get isUserAdmin(): boolean {
    return this.authService.getUserRole === 'admin';
  }

  handleEdit(obra: any) {
    // Tu lógica para manejar la edición de una obra
  }

  handleDelete(obra: any) {
    // Tu lógica para manejar la eliminación de una obra
  }
}
 
 

<main>
  <div class="container">
    <h1 class="text-center">Entradas para Funciones de Teatro</h1>
    <p class="text-center">Disfruta de las mejores representaciones en el Teatro Local</p>
    <br>
    <br>
    <div class="show-cards-container">
      <div *ngFor="let show of shows">

        <app-show-card [showImageUrl]="show.imagen_url"
                       [bookingUrl]="show.booking_url"
                       [showDescription]="show.descripcion">
        </app-show-card>

        <div *ngIf="isUserAdmin" class="admin-buttons">
          <button class="btn btn-warning" (click)="handleEdit(shows)">Modificar</button>
          <button class="btn btn-danger" (click)="handleDelete(shows)">Eliminar</button>
        </div>
      </div>
    </div>

  </div>

  <div routerLink="/add-obra" class="text-center custom-spacing" *ngIf="isUserAdmin">
    <button class="btn btn-success custom-btn-size">
      Añadir Obra
    </button>
  </div>

</main>
