import { Component, Input } from '@angular/core';
import { AuthenticationService } from '../services/authentication.service';

@Component({
  selector: 'app-show-card',
  templateUrl: './show-card.component.html',
  styleUrls: ['./show-card.component.css']
})
export class ShowCardComponent {
    @Input() showImageUrl: string = '';
    @Input() bookingUrl: string = '';
    @Input() showDescription: string = '';
    isAdmin: boolean = false;

  
  
    constructor(private authService: AuthenticationService) {
      // Suscríbete a los cambios de estado de la sesión y el rol del usuario
      this.authService._isLoggedIn.subscribe(_isLoggedIn => {
        this.isAdmin = _isLoggedIn && this.authService.getUserRole === 'admin';
      });
    }
   
    
  showButton: boolean = false;
}
