import { Component } from '@angular/core';
import { ObraService } from '../services/obra.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-obra',
  templateUrl: './add-obra.component.html',
  styleUrls: ['./add-obra.component.css']
})
export class AddObraComponent {
  obra = {
    titulo: '',
    imagen_url: '',
    descripcion: ''
  };

  constructor(private obraService: ObraService, private router: Router) { }

  onSubmit() {
    this.obraService.addObra(this.obra).subscribe({
      next: (response) => {
        console.log('Obra agregada con éxito', response);
        this.router.navigate(['/']);
      },
      error: (error) => {
        console.error('Error al agregar obra', error);
        alert('Error al agregar la obra. Por favor, inténtalo de nuevo.');
      }
    });
  }
}
<div class="obra-form-container">
  <div *ngIf="obra.imagen_url" class="image-preview">
    <img [src]="obra.imagen_url" alt="Vista previa de la imagen" />
  </div>

  <form (ngSubmit)="onSubmit()" class="obra-form">
    <div class="form-group">
      <label for="titulo">Título:</label>
      <input type="text" id="titulo" class="form-control" [(ngModel)]="obra.titulo" name="titulo" required>
    </div>
    <div class="form-group">
      <label for="imagen">Imagen URL:</label>
      <input type="text" id="imagen" class="form-control" [(ngModel)]="obra.imagen_url" name="imagen" required>
    </div>
    <div class="form-group">
      <label for="descripcion">Descripción:</label>
      <textarea id="descripcion" class="form-control" [(ngModel)]="obra.descripcion" name="descripcion" required></textarea>
    </div>
    <button type="submit" class="btn btn-primary">Agregar Obra</button>
  </form>
</div>
