import { Component } from '@angular/core';
import { AuthenticationService } from '../services/authentication.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  nombre_usuario: string = '';
  contrasena: string = '';

  constructor(private authService: AuthenticationService) {}

  onSubmit() {
    this.authService.login({ nombre_usuario: this.nombre_usuario, contrasena: this.contrasena })
      .subscribe({
        next: (response) => {
          console.log(response);
          // Aquí manejarías la lógica de éxito, como almacenar el token y redirigir al usuario.
        },
        error: (error) => {
          console.error(error);
          // Aquí manejarías la lógica de error, como mostrar un mensaje al usuario.
        }
      });
  }
}
