import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
// ...


@Component({
  selector: 'app-detalles-obra',
  templateUrl: './detalles-obra.component.html',
  styleUrls: ['./detalles-obra.component.css']
})
export class DetallesObraComponent  {
  constructor(private route: ActivatedRoute) { }

  ngOnInit() {
    this.route.params.subscribe(params => {
       params['id']; // El '+' convierte el parámetro en un número
      // Aquí puedes añadir la lógica para cargar los detalles de la obra
    });
  }
}


<div class="container">
  <h1>{{ obra.titulo }}</h1>
  <div class="details-container">
    <img [src]="obra.imagenUrl" alt="Imagen de la obra" class="obra-imagen">
    <p>{{ obra.descripcion }}</p>
    <div class="reserva-box">
      <label for="reservaCantidad">Reservas:</label>
      <select id="reservaCantidad" >
        <option *ngFor="let num of [1,2,3,4,5]" [value]="num">{{num}}</option>
      </select>
      <button (click)="hacerReserva()">Confirmar Reserva</button>
    </div>
  </div>
</div>
