import { Component, Input } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-show-card',
  templateUrl: './show-card.component.html',
  styleUrls: ['./show-card.component.css']
})


export class ShowCardComponent {
    @Input() showImageUrl: string = '';
    @Input() showId: string = '';
    @Input() bookingUrl: string = '';
    @Input() showDescription: string = '';
    @Input() id: number = 0;

    constructor(private router: Router) {}

    goToDetails() {
      if (this.id !== undefined && this.id !== 0) {
        this.router.navigate(['/detalles-obra', this.id]);
      } else {
        // Manejar el caso en que id no sea válido
      }
    }
    
  showButton: boolean = false;
}

<div class="show-card" 
     (mouseenter)="showButton = true" 
     (mouseleave)="showButton = false"
     (click)="goToDetails()">
  <a    class="show-card-link">
    <img [src]="showImageUrl" [alt]="showDescription" class="img-fluid show-card-image" (click)="goToDetails(); $event.preventDefault()">
  </a>
  <button *ngIf="showButton" class="btn btn-primary show-card-button"  [routerLink]="['/detalles-obra', id]" > 
    
    Comprar Entradas
  </button>
</div>
