import { Component, OnInit } from '@angular/core';
import { ObraService } from './services/obra.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit {
  title = 'TeatroSinFiltros';
  shows: any[] = [];

  constructor(private obraService: ObraService) { }

  ngOnInit() {
    this.obraService.getObras().subscribe(
      (data) => {
        console.log(data);
        this.shows = data;
      },
      (error) => {
        console.error('Error al obtener obras', error);
      }
    );
  }
}


    <main>
      <div class="container">
        <h1 class="text-center">Entradas para Funciones de Teatro</h1>
        <p class="text-center">Disfruta de las mejores representaciones en el Teatro Local</p>
        <br>
        <br>
        <div class="show-cards-container">
            <app-show-card 
            *ngFor="let show of shows"
            [showImageUrl]="show.imagen_url"
            [bookingUrl]="show.booking_url" 
            [showDescription]="show.descripcion">
          </app-show-card>
        </div>
      </div>
    </main>
